import React, { useState, useEffect } from 'react';

function BrokerList() {
    const [brokers, setBrokers] = useState([]);
    const [newBroker, setNewBroker] = useState([]);

    const loadBrokers = () => {
        fetch('http://localhost:3001/brokers')
            .then((response) => response.json())
            .then((data) => setBrokers(data))
            .catch((error) => console.error('Error loading brokers:', error));
    };

    // Используем useEffect для загрузки брокеров при загрузке компонента
    useEffect(() => {
        loadBrokers();
    }, []);

    // Функция для добавления брокера
    const addBroker = () => {
        if (newBroker.name.trim()) {
            const broker = { id: Date.now().toString(), name: newBroker.name, balance: newBroker.balance }; // Генерируем id
            fetch('http://localhost:3001/brokers', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(broker),
            })
                .then((response) => response.json())
                .then(() => {
                    setNewBroker('');
                    loadBrokers(); // Перезагружаем список брокеров
                })
                .catch((error) => console.error('Error adding broker:', error));
        }
    };

    // Функция для удаления брокера
    const deleteBroker = (id) => {
        fetch(`http://localhost:3001/brokers/${id}`, {
            method: 'DELETE',
        })
            .then(() => loadBrokers()) // Перезагружаем список брокеров после удаления
            .catch((error) => console.error('Error deleting broker:', error));
    };

    return (
        <div>
            <h2>Список брокеров</h2>
            <ul>
                {brokers.map((broker) => (
                    <li key={broker.id}>
                        {broker.name} - Баланс: {broker.balance}
                        <button onClick={() => deleteBroker(broker.id)}>Удалить</button>
                    </li>
                ))}
            </ul>
            <input
                type="text"
                value={newBroker.name}
                onChange={(e) => setNewBroker(e.target.value)}
                placeholder="Добавьте нового брокера"
            />
            <input
            type="number"
            value={newBroker.balance}
            onClick={(e) => addBroker(e)}/>
            <button onClick={addBroker}>Добавить</button>
        </div>
    );
}

export default BrokerList;
