import React, {useState} from 'react';

function BrokerList() {
    const [brokers, setBrokers] = useState([]);
    const [newBroker, setNewBroker] = useState("");

    const addBroker = () => {
        if (newBroker.trim()) {
            setBrokers([...brokers, newBroker]);
            setNewBroker("");
        }
    }
    const deleteBroker = (indexToRemove) => {
        setBrokers(brokers.filter((_, index) => index !== indexToRemove));
    }

    return (
        <div>
            <h2>Список брокеров</h2>
            <ul>
                {brokers.map((task, index) => (
                    <li key={index}>
                        {task}
                        <button onClick={() => deleteBroker(index)}>Удалить</button>
                    </li>
                ))}
            </ul>
            <input
                type="text"
                value={newBroker}
                onChange={(e) => setNewBroker(e.target.value)}
                placeholder="Добавьте новую задачу"
            />
            <button onClick={addBroker}>Добавить</button>
        </div>
    );
}
export default BrokerList;
