import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './ExchangeSimulator.css';
import { Line } from 'react-chartjs-2'; // Импортируем компонент для графика
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

// Регистрируем необходимые компоненты для Chart.js
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const socket = io('http://localhost:3001', {
    transports: ['websocket'],
});

const ExchangeSimulator = () => {
    const [stocks, setStocks] = useState([]);
    const [speed, setSpeed] = useState(1);
    const [isTrading, setIsTrading] = useState(false);
    const [stockData, setStockData] = useState({
        labels: [], // Метки для оси X (например, временные метки)
        datasets: [
            {
                label: 'AAPL (Apple)',
                data: [], // Данные для цен акций
                fill: false,
                borderColor: 'rgba(75,192,192,1)',
                tension: 0.1,
            },
            {
                label: 'SBUX (Starbucks)',
                data: [],
                fill: false,
                borderColor: 'rgba(255,99,132,1)',
                tension: 0.1,
            },
        ],
    });

    useEffect(() => {
        socket.on('connect', () => {
            console.log('WebSocket connected');
        });

        socket.on('connect_error', (err) => {
            console.log('WebSocket connection error:', err);
        });

        socket.on('disconnect', () => {
            console.log('WebSocket disconnected');
        });

        socket.on('stockUpdate', (updatedStocks) => {
            console.log('Received updated stocks:', updatedStocks);
            setStocks(updatedStocks);

            // Обновляем данные на графике
            const newLabels = [...stockData.labels, new Date().toLocaleTimeString()]; // Добавляем метку времени
            const newDataAAPL = [...stockData.datasets[0].data, updatedStocks[0].price]; // Добавляем цену Apple
            const newDataSBUX = [...stockData.datasets[1].data, updatedStocks[1].price]; // Добавляем цену Starbucks

            setStockData({
                labels: newLabels,
                datasets: [
                    {
                        ...stockData.datasets[0],
                        data: newDataAAPL,
                    },
                    {
                        ...stockData.datasets[1],
                        data: newDataSBUX,
                    },
                ],
            });
        });

        socket.emit('getStocks');  // Запрос на получение начальных данных

        return () => {
            socket.disconnect();
        };
    }, [stockData]);

    const startTrading = () => {
        setIsTrading(true);
        socket.emit('startTrading', { speed });
    };

    const stopTrading = () => {
        setIsTrading(false);
        socket.emit('stopTrading');
    };

    return (
        <div className="content">
            <header className="header">Биржевые торги</header>

            <div className="settings">
                <label htmlFor="speed">Скорость обновления (сек):</label>
                <input
                    id="speed"
                    type="number"
                    min="1"
                    value={speed}
                    onChange={(e) => setSpeed(Number(e.target.value))}
                    disabled={isTrading}
                />

                <button onClick={startTrading} disabled={isTrading}>
                    Начать торги
                </button>
                <button onClick={stopTrading} disabled={!isTrading}>
                    Остановить торги
                </button>
            </div>

            <div className="stock-prices">
                <h3>Текущие цены акций:</h3>
                <table className="stockTable">
                    <thead>
                    <tr>
                        <th>Компания</th>
                        <th>Тикер</th>
                        <th>Цена ($)</th>
                    </tr>
                    </thead>
                    <tbody>
                    {stocks.map((stock) => (
                        <tr key={stock.ticker}>
                            <td>{stock.companyName}</td>
                            <td>{stock.ticker}</td>
                            <td>{stock.price.toFixed(2)}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>

            <div className="chart">
                <h3>График изменения цен:</h3>
                <Line data={stockData} />
            </div>
        </div>
    );
};

export default ExchangeSimulator;
