import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import io from 'socket.io-client';
import { setStocks, updateStockPrices, setTradingStatus, setSpeed } from './stockSlice';
import { Line } from 'react-chartjs-2';
import './ExchangeSimulator.css';

const socket = io('http://localhost:3001', {
    transports: ['websocket'],
});

const ExchangeSimulator = () => {
    const { stocks, isTrading, speed } = useSelector((state) => state.stocks);
    const dispatch = useDispatch();

    const [selectedStock, setSelectedStock] = useState(null); // Хранит текущую акцию для отображения графика

    useEffect(() => {
        socket.on('stockUpdate', (updatedStocks) => {
            dispatch(setStocks(updatedStocks)); // Обновление акций в Redux
        });

        socket.emit('loadPage');
        socket.emit('getStocks');  // Запрос на получение данных

        return () => {
            socket.off('stockUpdate'); // Очистка события
        };
    }, [dispatch]);

    const startTrading = () => {
        dispatch(setTradingStatus(true));
        socket.emit('startTrading', { speed });
    };

    const stopTrading = () => {
        dispatch(setTradingStatus(false));
        socket.emit('stopTrading');
    };

    const handleSpeedChange = (e) => {
        dispatch(setSpeed(Number(e.target.value)));
    };

    const handleShowChart = (stock) => {
        setSelectedStock(stock); // Устанавливаем выбранную акцию для отображения графика
    };

    const handleCloseChart = () => {
        setSelectedStock(null); // Закрыть график
    };

    return (
        <div className="content">
            <header className="header">Биржевые торги</header>

            <div className="settings">
                <label htmlFor="speed">Скорость обновления (сек):</label>
                <input
                    id="speed"
                    type="number"
                    min="1"
                    value={speed}
                    onChange={handleSpeedChange}
                    disabled={isTrading}
                />

                <button onClick={startTrading} disabled={isTrading}>
                    Начать торги
                </button>
                <button onClick={stopTrading} disabled={!isTrading}>
                    Остановить торги
                </button>
            </div>

            <div className="stock-prices">
                <h3>Текущие цены акций:</h3>
                <table className="stockTable">
                    <thead>
                    <tr>
                        <th>Компания</th>
                        <th>Тикер</th>
                        <th>Цена ($)</th>
                        <th>Действие</th>
                    </tr>
                    </thead>
                    <tbody>
                    {stocks.map((stock) => (
                        <tr key={stock.ticker}>
                            <td>{stock.companyName}</td>
                            <td>{stock.ticker}</td>
                            <td>{stock.price.toFixed(2)}</td>
                            <td>
                                <button onClick={() => handleShowChart(stock)}>График</button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>

            {/* Отображение графика */}
            {selectedStock && (
                <div className="chart-container">
                    <h3>График изменений для {selectedStock.companyName} ({selectedStock.ticker})</h3>
                    <Line
                        data={{
                            labels: selectedStock.historicalData.map((_, index) => `Точка ${index + 1}`),
                            datasets: [
                                {
                                    label: 'Цена ($)',
                                    data: selectedStock.historicalData.map((dataPoint) => dataPoint.price),
                                    borderColor: 'rgba(75,192,192,1)',
                                    borderWidth: 2,
                                    fill: false,
                                },
                            ],
                        }}
                        options={{
                            responsive: true,
                            maintainAspectRatio: false,
                            scales: {
                                y: {
                                    beginAtZero: false, // Начало оси Y с минимального значения данных
                                    ticks: {
                                        precision: 2, // Количество знаков после запятой
                                    },
                                    suggestedMin: Math.min(...selectedStock.historicalData.map((d) => d.price)) * 0.95, // 5% меньше минимального значения
                                    suggestedMax: Math.max(...selectedStock.historicalData.map((d) => d.price)) * 1.05, // 5% больше максимального значения
                                },
                                x: {
                                    title: {
                                        display: true,
                                        text: 'Точки времени',
                                    },
                                },
                            },
                            plugins: {
                                legend: {
                                    display: true,
                                    position: 'top',
                                },
                                tooltip: {
                                    callbacks: {
                                        label: function (context) {
                                            return `$${context.raw.toFixed(2)}`;
                                        },
                                    },
                                },
                            },
                        }}
                    />

                    <button onClick={handleCloseChart}>Закрыть</button>
                </div>
            )}
        </div>
    );
};

export default ExchangeSimulator;
