import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './styles.css'; // Подключение файла со стилями

const ExchangeSimulator = () => {
    const [startDate, setStartDate] = useState('2022-11-01');
    const [speed, setSpeed] = useState(1); // скорость в секундах
    const [isTrading, setIsTrading] = useState(false);
    const [currentDate, setCurrentDate] = useState(startDate);
    const [stocks, setStocks] = useState([
        { ticker: 'AAPL', companyName: 'Apple, Inc.', price: 151.89 },
        { ticker: 'SBUX', companyName: 'Starbucks, Inc.', price: 112.47 },
        // Дополнительные акции
    ]);
    const [socket, setSocket] = useState(null);

    useEffect(() => {
        const newSocket = io('http://localhost:3001');
        setSocket(newSocket);

        return () => newSocket.close();
    }, []);

    const startTrading = () => {
        setIsTrading(true);
        simulateTrading();
    };

    const simulateTrading = () => {
        if (!isTrading) return;

        setInterval(() => {
            const nextDate = new Date(currentDate);
            nextDate.setDate(nextDate.getDate() + 1);
            setCurrentDate(nextDate.toISOString().split('T')[0]);

            const updatedStocks = stocks.map(stock => ({
                ...stock,
                price: Math.max(0, stock.price + (Math.random() - 0.5) * 5), // Цены не могут быть отрицательными
            }));
            setStocks(updatedStocks);

            socket.emit('updateStockPrices', updatedStocks);
        }, speed * 1000);
    };

    return (
        <div className="content">
            <header className="header">Биржевые торги</header>

            <div className="settings">
                <label htmlFor="start-date">Дата начала торгов:</label>
                <input
                    id="start-date"
                    type="date"
                    value={startDate}
                    onChange={(e) => setStartDate(e.target.value)}
                />

                <label htmlFor="speed">Скорость смены дат (сек):</label>
                <input
                    id="speed"
                    type="number"
                    min="1"
                    value={speed}
                    onChange={(e) => setSpeed(Number(e.target.value))}
                />

                <button
                    className="toggle-btn"
                    onClick={startTrading}
                    disabled={isTrading}
                >
                    Начало торгов
                </button>
            </div>

            <div className="trading-info">
                <h3>Текущая дата торгов:</h3>
                <p>{currentDate}</p>
            </div>

            <div className="stock-prices">
                <h3>Текущие цены акций:</h3>
                <table className="stockTable">
                    <thead>
                    <tr>
                        <th>Компания</th>
                        <th>Тикер</th>
                        <th>Цена ($)</th>
                    </tr>
                    </thead>
                    <tbody>
                    {stocks.map((stock) => (
                        <tr key={stock.ticker}>
                            <td>{stock.companyName}</td>
                            <td>{stock.ticker}</td>
                            <td>{stock.price.toFixed(2)}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default ExchangeSimulator;
